CustomField.formula:
IF(
    /* 1) If UTM Source is populated */
    NOT(ISBLANK(utm_source__c)),
    utm_source__c & " " & TEXT(Campaign_Brand__c),

    /* 2) Else, pick by Sales_Office__c */
    IF(
        Sales_Office__c = "Everyday Debt",
        IF(
            NOT(ISBLANK(TEXT(Lead_Source_EDD__c))),
            TEXT(Lead_Source_EDD__c) & " " & TEXT(Campaign_Brand__c),
            TEXT(Source__c)         & " " & TEXT(Campaign_Brand__c)
        ),
        IF(
            Sales_Office__c = "Next Day Settlement",
            IF(
                NOT(ISBLANK(TEXT(Lead_Source_Debtifi__c))),
                TEXT(Lead_Source_Debtifi__c) & " " & TEXT(Campaign_Brand__c),
                TEXT(Source__c)              & " " & TEXT(Campaign_Brand__c)
            ),
            IF(
                Sales_Office__c = "Debt Crusaders",
                IF(
                    NOT(ISBLANK(TEXT(Lead_Source_DC__c))),
                    TEXT(Lead_Source_DC__c) & " " & TEXT(Campaign_Brand__c),
                    TEXT(Source__c)        & " " & TEXT(Campaign_Brand__c)
                ),
                /* Default for any other office */
                TEXT(Source__c) & " " & TEXT(Campaign_Brand__c)
            )
        )
    )
)
