ValidationRule.errorConditionFormula:
AND(
  /* Only for Debt Crusaders office */
  Sales_Office__c = "Debt Crusaders",

  /* Stage is Docs submitted to UW or beyond */
  OR(
    ISPICKVAL(StageName, "Docs submitted to UW"),
    ISPICKVAL(StageName, "Ready to Close"),
    ISPICKVAL(StageName, "Contract Sent"),
    ISPICKVAL(StageName, "Contract Signed"),
    ISPICKVAL(StageName, "Enrolled")
  ),

  /* One of the two required fields is blank */
  OR(
    ISPICKVAL(Lead_Source_DC__c, ""),
    ISBLANK(Desired_Weekly_Payment__c)
  )
)
